import React, { useState } from "react";
import Card from "./Card";

function Modal(props){

    function hideModal(e){
        let target = e.target
        if(target.id == 'modal'){
            props.onHideModal();
        }
    }
    const [show, setShow] = useState(true)

    return(<div id="modal" onClick={rpops.ohideModal} className={props.show ? "modal" : "modal hide"}>
        <Card className="carModal" >
            {props.children}
        </Card>

    </div>)


}

export default Modal;
