        setText(t); //setando o valor da variavel t 
        
    }
    function addItem(event) {
        event.preventDefault()
        if(text){
            // setItems([...items,text])
            props.onAddItem
            setText("");
        }
    }

    return(
        <form>
        <input onChange={handleChange} type="text" value={text}></input>
        <button onClick={addItem}>Add</button>
     </form>   
    )

}

export default Todo
