import React from 'react';

function List(props){

    
    return(
        <ul>
            {/* <li>{text}</li> exibindo o valor do input na lista  */}
            {props.items.map(item=><li key={item.id}>
                {item.text}
                <button onClick={(deleteItem(item))}><img src='./assets/bin.png'></img></button>
            </li>)}             
        </ul>
    )
    
}

export default List;
